import React, { useState } from 'react';
import axios from 'axios';

export default function LeaderDashboard() {
  const [auctionData, setAuctionData] = useState({
    leaderEmail: '',
    tournamentName: '',
    tournamentDate: '',
    teamsCount: 0,
    totalPointsPerTeam: 0,
    players: [
      {
        name: '',
        number: 0,
        team: '',
        bidPoints: 0,
        profilePic: '',
      },
    ],
    image: null, // For handling the uploaded image
  });

  // Function to handle input change
  const handleChange = (e) => {
    const { name, value } = e.target;
    setAuctionData({
      ...auctionData,
      [name]: value,
    });
  };

  // Handle file input change
  const handleImageChange = (e) => {
    setAuctionData({
      ...auctionData,
      image: e.target.files[0], // Store the selected image file
    });
  };

  // Function to handle player input change
  const handlePlayerChange = (e, index) => {
    const { name, value } = e.target;
    const updatedPlayers = [...auctionData.players];
    updatedPlayers[index] = { ...updatedPlayers[index], [name]: value };
    setAuctionData({
      ...auctionData,
      players: updatedPlayers,
    });
  };

  // Function to add a new player form
  const addPlayer = () => {
    setAuctionData({
      ...auctionData,
      players: [
        ...auctionData.players,
        {
          name: '',
          number: 0,
          team: '',
          bidPoints: 0,
          profilePic: '',
        },
      ],
    });
  };

  // Function to remove a player form
  const removePlayer = (index) => {
    const updatedPlayers = auctionData.players.filter((_, i) => i !== index);
    setAuctionData({
      ...auctionData,
      players: updatedPlayers,
    });
  };

  // Function to handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const formData = new FormData();
      formData.append('leaderEmail', auctionData.leaderEmail);
      formData.append('tournamentName', auctionData.tournamentName);
      formData.append('tournamentDate', auctionData.tournamentDate);
      formData.append('teamsCount', auctionData.teamsCount);
      formData.append('totalPointsPerTeam', auctionData.totalPointsPerTeam);

      // Append players array
      auctionData.players.forEach((player, index) => {
        formData.append(`players[${index}][name]`, player.name);
        formData.append(`players[${index}][number]`, player.number);
        formData.append(`players[${index}][team]`, player.team);
        formData.append(`players[${index}][bidPoints]`, player.bidPoints);
        formData.append(`players[${index}][profilePic]`, player.profilePic);
      });

      // Add image if available
      if (auctionData.image) {
        formData.append('image', auctionData.image);
      }

      const response = await axios.post('http://localhost:5000/api/auctions/create-auction', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      // Handle response after success
      console.log('Auction created successfully', response.data);
    } catch (error) {
      console.error('Error creating auction:', error);
    }
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h1 style={{ textAlign: 'center', color: '#333' }}>Create New Auction</h1>
      <form onSubmit={handleSubmit} style={{ maxWidth: '600px', margin: '0 auto', backgroundColor: '#f9f9f9', padding: '20px', borderRadius: '8px', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)' }}>
        <div style={{ marginBottom: '15px' }}>
          <input
            type="email"
            name="leaderEmail"
            value={auctionData.leaderEmail}
            onChange={handleChange}
            placeholder="Leader Email"
            required
            style={{ width: '100%', padding: '10px', border: '1px solid #ccc', borderRadius: '4px' }}
          />
        </div>

        <div style={{ marginBottom: '15px' }}>
          <input
            type="text"
            name="tournamentName"
            value={auctionData.tournamentName}
            onChange={handleChange}
            placeholder="Tournament Name"
            required
            style={{ width: '100%', padding: '10px', border: '1px solid #ccc', borderRadius: '4px' }}
          />
        </div>

        <div style={{ marginBottom: '15px' }}>
          <input
            type="date"
            name="tournamentDate"
            value={auctionData.tournamentDate}
            onChange={handleChange}
            required
            style={{ width: '100%', padding: '10px', border: '1px solid #ccc', borderRadius: '4px' }}
          />
        </div>

        <div style={{ marginBottom: '15px' }}>
          <input
            type="number"
            name="teamsCount"
            value={auctionData.teamsCount}
            onChange={handleChange}
            placeholder="Teams Count"
            required
            style={{ width: '100%', padding: '10px', border: '1px solid #ccc', borderRadius: '4px' }}
          />
        </div>

        <div style={{ marginBottom: '15px' }}>
          <input
            type="number"
            name="totalPointsPerTeam"
            value={auctionData.totalPointsPerTeam}
            onChange={handleChange}
            placeholder="Total Points Per Team"
            required
            style={{ width: '100%', padding: '10px', border: '1px solid #ccc', borderRadius: '4px' }}
          />
        </div>

        {/* Render Player Info dynamically */}
        <div style={{ marginBottom: '20px' }}>
          <h3 style={{ color: '#333' }}>Player Info</h3>
          {auctionData.players.map((player, index) => (
            <div key={index} style={{ marginBottom: '15px', padding: '15px', backgroundColor: '#f1f1f1', borderRadius: '5px' }}>
              <h4>Player {index + 1}</h4>
              <input
                type="text"
                name="name"
                value={player.name}
                onChange={(e) => handlePlayerChange(e, index)}
                placeholder="Player Name"
                required
                style={{ width: '100%', padding: '10px', border: '1px solid #ccc', borderRadius: '4px', marginBottom: '10px' }}
              />
              <input
                type="number"
                name="number"
                value={player.number}
                onChange={(e) => handlePlayerChange(e, index)}
                placeholder="Player Number"
                required
                style={{ width: '100%', padding: '10px', border: '1px solid #ccc', borderRadius: '4px', marginBottom: '10px' }}
              />
              <input
                type="text"
                name="team"
                value={player.team}
                onChange={(e) => handlePlayerChange(e, index)}
                placeholder="Player Team"
                required
                style={{ width: '100%', padding: '10px', border: '1px solid #ccc', borderRadius: '4px', marginBottom: '10px' }}
              />
              <input
                type="number"
                name="bidPoints"
                value={player.bidPoints}
                onChange={(e) => handlePlayerChange(e, index)}
                placeholder="Bid Points"
                required
                style={{ width: '100%', padding: '10px', border: '1px solid #ccc', borderRadius: '4px', marginBottom: '10px' }}
              />
              <input
                type="text"
                name="profilePic"
                value={player.profilePic}
                onChange={(e) => handlePlayerChange(e, index)}
                placeholder="Profile Picture URL"
                style={{ width: '100%', padding: '10px', border: '1px solid #ccc', borderRadius: '4px', marginBottom: '10px' }}
              />
              <button
                type="button"
                onClick={() => removePlayer(index)}
                style={{ backgroundColor: '#ff4d4d', color: '#fff', padding: '5px 10px', border: 'none', borderRadius: '4px', cursor: 'pointer' }}
              >
                Remove Player
              </button>
            </div>
          ))}
          <button
            type="button"
            onClick={addPlayer}
            style={{ backgroundColor: '#4CAF50', color: '#fff', padding: '10px 20px', border: 'none', borderRadius: '4px', cursor: 'pointer' }}
          >
            Add Another Player
          </button>
        </div>

        {/* Image Upload */}
        <div style={{ marginBottom: '15px' }}>
          <input
            type="file"
            onChange={handleImageChange}
            style={{ padding: '10px', border: '1px solid #ccc', borderRadius: '4px' }}
          />
        </div>

        <button
          type="submit"
          style={{
            backgroundColor: '#008CBA',
            color: '#fff',
            padding: '10px 20px',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer',
            width: '100%',
            fontSize: '16px',
          }}
        >
          Submit Auction
        </button>
      </form>
    </div>
  );
}
