import React, { useState } from 'react';

export default function TournamentForm() {
  const [formData, setFormData] = useState({
    leaderEmail: '',
    tournamentName: '',
    teamsCount: 0,
    bidPointsPerTeam: 0,
    teams: [''],
  });

  // Handle input change
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  // Handle team names input change
  const handleTeamChange = (index, value) => {
    const updatedTeams = [...formData.teams];
    updatedTeams[index] = value;
    setFormData({
      ...formData,
      teams: updatedTeams,
    });
  };

  // Add a new team input field
  const addTeam = () => {
    setFormData({
      ...formData,
      teams: [...formData.teams, ''],
    });
  };

  // Remove a team input field
  const removeTeam = (index) => {
    const updatedTeams = formData.teams.filter((_, i) => i !== index);
    setFormData({
      ...formData,
      teams: updatedTeams,
    });
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form submitted:', formData);
  };

  return (
    <div>
      <h1>Tournament Form</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          name="leaderEmail"
          value={formData.leaderEmail}
          onChange={handleChange}
          placeholder="Leader Email"
          required
        />
        <input
          type="text"
          name="tournamentName"
          value={formData.tournamentName}
          onChange={handleChange}
          placeholder="Tournament Name"
          required
        />
        <input
          type="number"
          name="teamsCount"
          value={formData.teamsCount}
          onChange={handleChange}
          placeholder="Number of Teams"
          required
        />
        <input
          type="number"
          name="bidPointsPerTeam"
          value={formData.bidPointsPerTeam}
          onChange={handleChange}
          placeholder="Bid Points Per Team"
          required
        />

        {/* Render Team Names dynamically */}
        <div>
          <h3>Team Names</h3>
          {formData.teams.map((team, index) => (
            <div key={index}>
              <input
                type="text"
                value={team}
                onChange={(e) => handleTeamChange(index, e.target.value)}
                placeholder={`Team ${index + 1} Name`}
                required
              />
              {formData.teams.length > 1 && (
                <button type="button" onClick={() => removeTeam(index)}>Remove Team</button>
              )}
            </div>
          ))}
          <button type="button" onClick={addTeam}>Add Another Team</button>
        </div>

        <button type="submit">Submit Tournament</button>
      </form>
    </div>
  );
}
