import React from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Navbar = () => {
    const navigate = useNavigate();

    // Get user role and email from local storage
    const userRole = localStorage.getItem('role');
    const userEmail = localStorage.getItem('userEmail');

    // Handle logout
    const handleLogout = () => {
        localStorage.removeItem('userEmail');
        localStorage.removeItem('role');
        navigate('/');
    };

    // Handle scrolling to sections
    const scrollToSection = (sectionId) => {
        const section = document.getElementById(sectionId);
        if (section) {
            section.scrollIntoView({ behavior: 'smooth' });
        }
    };

    return (
        <header className="navbar">
            <h2 className="logo">CrickBidders</h2>
            <nav className="nav-links">
                <Link to="/" className="nav-link">Home</Link>

                {(userRole === 'leader' || userRole === 'admin') && (
                    <Link to="/leader-dashboard" className="nav-link">Leader</Link>
                )}

                {userRole === 'admin' && (
                    <Link to="/admin-dashboard" className="nav-link">Admin</Link>
                )}

                {userRole === 'viewer' && (
                    <button onClick={() => navigate('/request-role')} className="nav-link">Create New</button>
                )}

                {userEmail && userRole ? (
                    <button onClick={handleLogout} className="nav-link logout-button">Logout</button>
                ) : (
                    <Link to="/login" className="nav-link">Log In</Link>
                )}

                <button onClick={() => scrollToSection('about')} className="nav-link">About</button>
                <button onClick={() => scrollToSection('how-to-use')} className="nav-link">How</button>
                <button onClick={() => scrollToSection('contact')} className="nav-link">Contact Us</button>
            </nav>
        </header>
    );
};

export default Navbar;
