import React, { useState, useContext } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { loginUser } from '../api';
import { AuthContext } from '../context/AuthContext';

const Login = () => {
    const [formData, setFormData] = useState({ email: '', password: '', role: 'viewer' });
    const { login } = useContext(AuthContext);
    const navigate = useNavigate();
    const [error, setError] = useState('');

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const { data } = await loginUser(formData);  // Send role along with email and password
            login(data.user);  // Set the user data to the context
            localStorage.setItem('userEmail', formData.email);  // Store email in local storage
            localStorage.setItem('userRole', formData.role); // Store selected role

            // Navigate based on the selected role
            if (formData.role === 'viewer') navigate('/viewer-dashboard');
            else if (formData.role === 'leader') navigate('/payment');
            else if (formData.role === 'admin') navigate('/admin-dashboard');
        } catch (error) {
            setError("Login failed. Please check your credentials.");
            console.error("Login Error:", error);
        }
    };

    return (
        <div className="login-container">
            <h2>Log In to CrickBidders</h2>
            <form class="form">
    <div class="flex-column">
      <label for="email">Email</label>
      <div class="inputForm">
        <input id="email" placeholder="Enter your Email" class="input" type="email" name="email" required />
      </div>
    </div>

    <div class="flex-column">
      <label for="password">Password</label>
      <div class="inputForm">
        <input id="password" placeholder="Enter your Password" class="input" type="password" name="password" required />
      </div>
    </div>

    <div class="flex-row">
      <div>
        <input type="radio" id="rememberMe" name="rememberMe" />
        <label for="rememberMe">Remember me</label>
      </div>
      <span class="span">Forgot password?</span>
    </div>

    <button class="button-submit" type="submit">Sign In</button>

    <p class="p">Don't have an account? <span class="span">Sign Up</span></p>
    <p class="p line">Or With</p>

    <div class="flex-row">
      <button class="btn google">Google</button>
      <button class="btn apple">Apple</button>
    </div>
</form>

            <p className="signup-link">
                Donâ€™t have an account? <Link to="/signup">Sign Up</Link>
            </p>
        </div>
    );
};

export default Login;
