import React, { useState } from 'react';

const RoleRequestForm = () => {
    const [formData, setFormData] = useState({
        fullName: '',
        tournamentName: '',
        tournamentDate: '',
        auctionDate: '',
        email: ''
    });

    // Handle form input changes
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({ ...prevData, [name]: value }));
    };

    // Handle form submission
    const handleFormSubmit = (e) => {
        e.preventDefault();
        console.log("Requesting role change with data:", formData);
        alert("Request sent to admin for role change to leader.");
        // You can also reset the form here if needed
    };

    return (
        <div className="form-container">
            <form onSubmit={handleFormSubmit} className="role-request-form">
                <h3>Request Role Change to Leader</h3>
                <label>
                    Full Name:
                    <input
                        type="text"
                        name="fullName"
                        value={formData.fullName}
                        onChange={handleInputChange}
                        required
                    />
                </label>
                <label>
                    Tournament Name:
                    <input
                        type="text"
                        name="tournamentName"
                        value={formData.tournamentName}
                        onChange={handleInputChange}
                        required
                    />
                </label>
                <label>
                    Tournament Date:
                    <input
                        type="date"
                        name="tournamentDate"
                        value={formData.tournamentDate}
                        onChange={handleInputChange}
                        required
                    />
                </label>
                <label>
                    Auction Date:
                    <input
                        type="date"
                        name="auctionDate"
                        value={formData.auctionDate}
                        onChange={handleInputChange}
                        required
                    />
                </label>
                <label>
                    Email Address:
                    <input
                        type="email"
                        name="email"
                        value={formData.email}
                        onChange={handleInputChange}
                        required
                    />
                </label>
                <button type="submit" className="submit-button">Submit Request</button>
            </form>
        </div>
    );
};

export default RoleRequestForm;
