import React, { useState } from 'react';
import { Container, Row, Col, Button, Form, Card } from 'react-bootstrap';
import { FaEnvelope } from 'react-icons/fa';

const LandingPage = () => {
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        message: ''
    });

    const [isSubmitting, setIsSubmitting] = useState(false);
    const [submissionStatus, setSubmissionStatus] = useState(null);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsSubmitting(true);

        // Simulate form submission - Replace with actual API call
        try {
            // Simulating a successful submission
            await new Promise((resolve) => setTimeout(resolve, 1000));
            setSubmissionStatus('Your message has been sent successfully!');
        } catch (error) {
            setSubmissionStatus('There was an error sending your message. Please try again later.');
        } finally {
            setIsSubmitting(false);
        }
    };

    return (
        <Container fluid className="landing-page" style={{ background: 'linear-gradient(100deg, #1ABC9C, #9ff5a3)', minHeight: '100vh', paddingBottom: '2rem' }}>
            <Row className="justify-content-center">
                <Col md={10} lg={8}>
                    <Card id="contact" className="my-5" style={{ backgroundColor: 'rgba(255, 255, 255, 0.9)', borderRadius: '15px', boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)' }}>
                        <Card.Body>
                            <Card.Title className="text-center"><h2 className="text-primary"><FaEnvelope className="me-2" />Contact Us</h2></Card.Title>
                            <Card.Text className="text-center mb-4">
                                If you have any questions or need support, feel free to reach out.
                            </Card.Text>

                            {/* Display submission status */}
                            {submissionStatus && (
                                <div className="text-center mb-3">
                                    <p className={submissionStatus.includes('success') ? 'text-success' : 'text-danger'}>
                                        {submissionStatus}
                                    </p>
                                </div>
                            )}

                            <Form onSubmit={handleSubmit}>
                                <Form.Group controlId="name" className="mb-3">
                                    <Form.Label>Name:</Form.Label>
                                    <Form.Control
                                        type="text"
                                        name="name"
                                        required
                                        value={formData.name}
                                        onChange={handleChange}
                                        placeholder="Enter your name"
                                    />
                                </Form.Group>

                                <Form.Group controlId="email" className="mb-3">
                                    <Form.Label>Email:</Form.Label>
                                    <Form.Control
                                        type="email"
                                        name="email"
                                        required
                                        value={formData.email}
                                        onChange={handleChange}
                                        placeholder="Enter your email"
                                    />
                                </Form.Group>

                                <Form.Group controlId="message" className="mb-3">
                                    <Form.Label>Message:</Form.Label>
                                    <Form.Control
                                        as="textarea"
                                        rows={3}
                                        name="message"
                                        required
                                        value={formData.message}
                                        onChange={handleChange}
                                        placeholder="Your message here"
                                    />
                                </Form.Group>

                                <div className="text-center">
                                    <Button variant="primary" type="submit" className="mt-3 px-4" disabled={isSubmitting}>
                                        {isSubmitting ? 'Sending...' : 'Send Message'}
                                    </Button>
                                </div>
                            </Form>
                        </Card.Body>
                    </Card>
                </Col>
            </Row>
        </Container>
    );
};

export default LandingPage;
