import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function ViewerDashboard() {
  const [auctions, setAuctions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Assume we have a way to fetch currentUserId (like from session storage or context)
  const currentUserId = sessionStorage.getItem('userId'); // Example, replace with actual method to get user ID

  useEffect(() => {
    if (!sessionStorage.getItem('hasVisited')) {
      sessionStorage.setItem('hasVisited', 'true');
      window.location.reload();
    }
  }, []);

  useEffect(() => {
    // API call to fetch auctions
    axios.get('http://localhost:5000/api/auction/auctions')
      .then(response => {
        setAuctions(response.data); // Save auctions data to state
        setLoading(false);           // Set loading to false once data is fetched
      })
      .catch(err => {
        setError('Failed to fetch auctions'); // Error handling
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div>
      <h1>Viewer Dashboard</h1>
      {auctions.length > 0 ? (
        <div>
          {auctions.map((auction) => (
            <div key={auction._id}>
              <h2>{auction.tournamentName}</h2>
              {/* Assuming you have a field like this in your backend, otherwise use the date field */}
              {/* <p>Date: {new Date(auction.tournamentDate).toLocaleDateString()}</p> */}

              {/* Display teams */}
              <div>
                <h3>Teams</h3>
                <ul>
                  {auction.teams.map((team, index) => (
                    <li key={index}>
                      <p>{team.name}</p>
                      <p>Bid Points: {team.bidPoints}</p>
                      <p>Remaining Bid Points: {team.remainingBidPoints}</p>
                    </li>
                  ))}
                </ul>
              </div>

              {/* Display players */}
              {auction.players && auction.players.length > 0 && (
                <div>
                  <h3>Players</h3>
                  <ul>
                    {auction.players.map((player) => (
                      <li key={player._id}>
                        {/* Assuming player has a profilePic field */}
                        <img src={player.photo} alt={player.name} width="50" />
                        <p>{player.name}</p>
                        <p>Team: {player.team}</p>
                        <p>Bid Amount: {player.bidAmount}</p>
                      </li>
                    ))}
                  </ul>
                </div>
              )}
            </div>
          ))}
        </div>
      ) : (
        <p>No auctions found.</p>
      )}
    </div>
  );
}
