import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const AdminSidebar = () => {
    // State to manage sidebar visibility
    const [isSidebarVisible, setSidebarVisible] = useState(true);

    // Function to handle link clicks
    const handleLinkClick = () => {
        // Hide the sidebar when any link is clicked
        setSidebarVisible(false);
    };

    return (
        <div className="d-flex">
            {/* Sidebar */}
            <aside
                className={`admin-sidebar bg-light p-4 border-end ${isSidebarVisible ? '' : 'd-none'}`}
                style={{ width: '250px', height: '100vh' }}
            >
                <h3 className="sidebar-title text-primary mb-4">Admin Dashboard</h3>
                <nav className="sidebar-nav">
                    <ul className="nav flex-column">
                        <li className="nav-item">
                            <Link to="/admin-sidebar" className="nav-link sidebar-link text-dark" onClick={handleLinkClick}>
                                Dashboard Overview
                            </Link>
                        </li>
                        <li className="nav-item">
                            <Link to="/admin-sidebar" className="nav-link sidebar-link text-dark" onClick={handleLinkClick}>
                                Users
                            </Link>
                        </li>
                        <li className="nav-item">
                            <Link to="/admin-sidebar" className="nav-link sidebar-link text-dark" onClick={handleLinkClick}>
                                Manage Auctions
                            </Link>
                        </li>
                        <li className="nav-item">
                            <Link to="/admin-sidebar" className="nav-link sidebar-link text-dark" onClick={handleLinkClick}>
                                Reports
                            </Link>
                        </li>
                        <li className="nav-item">
                            <Link to="/admin-sidebar" className="nav-link sidebar-link text-dark" onClick={handleLinkClick}>
                                Settings
                            </Link>
                        </li>
                    </ul>
                </nav>
            </aside>

            {/* Main Content */}
            <main
                className={`flex-grow-1 d-flex justify-content-center align-items-center p-4 ${
                    isSidebarVisible ? '' : 'w-100'
                }`}
                style={{ minHeight: '100vh' }}
            >
                <div className="container text-center">
                    <h1>Admin Dashboard</h1>
                    <p>Your content goes here.</p>
                </div>
            </main>
        </div>
    );
};

export default AdminSidebar;
