import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function ViewerDashboard() {
  const [auctions, setAuctions] = useState([]); // State to hold the auctions data
  const [loading, setLoading] = useState(true); // State to handle loading
  const [error, setError] = useState(null); // State for error handling
  const [playerDetails, setPlayerDetails] = useState({
    name: '',
    bidAmount: '',
    photo: '',
  }); // State for new player details
  const [selectedAuctionId, setSelectedAuctionId] = useState(null); // State to hold selected auction ID

  useEffect(() => {
    // Prevent page reload on first visit
    if (!sessionStorage.getItem('hasVisited')) {
      sessionStorage.setItem('hasVisited', 'true');
      window.location.reload();
    }
  }, []);

  useEffect(() => {
    axios
      .get('http://localhost:5000/api/auctions/')
      .then((response) => {
        setAuctions(response.data.auctions);
        setLoading(false);
      })
      .catch((err) => {
        setError('Failed to fetch auctions');
        setLoading(false);
      });
  }, []);

  // Function to handle player addition
  const handleAddPlayer = (auctionId) => {
    setSelectedAuctionId(auctionId);
    // Make API request to add player
    axios
      .post(`http://localhost:5000/api/auctions/${auctionId}/add-players`, playerDetails)
      .then((response) => {
        console.log('Player added:', response.data);
        // After successful player addition, refresh the auction list
        setAuctions(response.data.auctions);
        setPlayerDetails({ name: '', bidAmount: '', photo: '' }); // Clear input fields
      })
      .catch((err) => {
        setError('Failed to add player');
      });
  };

  // Handle input changes for new player form
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setPlayerDetails((prevDetails) => ({
      ...prevDetails,
      [name]: value,
    }));
  };

  if (loading) {
    return (
      <div className="d-flex justify-content-center align-items-center vh-100">
        <div className="spinner-border text-primary" role="status">
          <span className="sr-only">Loading...</span>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="alert alert-danger text-center" role="alert">
        {error}
      </div>
    );
  }

  return (
    <div className="container mt-4">
      <h1 className="text-center mb-4">Auction Dashboard</h1>
      {Array.isArray(auctions) && auctions.length > 0 ? (
        auctions.map((auction) => (
          <div key={auction._id} className="card mb-4 shadow-sm">
            <div className="card-header">
              <h2 className="card-title">{auction.tournamentName || "No Tournament Name"}</h2>
            </div>
            <div className="card-body">
              <p>
                <strong>Leader Email:</strong> {auction.leaderEmail || "No Email Provided"}
              </p>
              <p>
                <strong>Bid Points Per Team:</strong> {auction.bidPointsPerTeam || "N/A"}
              </p>

              <h3 className="mt-3">Teams:</h3>
              {auction.teams && auction.teams.length > 0 ? (
                <div className="row">
                  {auction.teams.map((team, index) => (
                    <div key={index} className="col-md-6">
                      <div className="card mb-3">
                        <div className="card-body">
                          <h4 className="card-title">{team.name || "No Team Name"}</h4>
                          <p>
                            <strong>Bid Points:</strong> {team.bidPoints}
                          </p>
                          <p>
                            <strong>Remaining Bid Points:</strong> {team.remainingBidPoints}
                          </p>
                          {team.image && (
                            <img
                              src={team.image}
                              alt={team.name}
                              className="img-fluid rounded"
                              style={{ maxWidth: '100px', height: 'auto' }}
                            />
                          )}
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <p>No teams available.</p>
              )}

              <h3 className="mt-3">Players:</h3>
              {auction.players && auction.players.length > 0 ? (
                <div className="row">
                  {auction.players.map((player, index) => (
                    <div key={index} className="col-md-4">
                      <div className="card mb-3">
                        <div className="card-body text-center">
                          <h4 className="card-title">{player.name || "No Player Name"}</h4>
                          <p>
                            <strong>Bid Amount:</strong> {player.bidAmount || "N/A"}
                          </p>
                          {player.photo && (
                            <img
                              src={player.photo}
                              alt={player.name}
                              className="img-fluid rounded-circle"
                              style={{ maxWidth: '50px', height: '50px' }}
                            />
                          )}
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <p>No players listed.</p>
              )}

              {/* Form to add a new player */}
              <div className="mt-4">
                <h4>Add Player to Auction</h4>
                <input
                  type="text"
                  name="name"
                  value={playerDetails.name}
                  onChange={handleInputChange}
                  placeholder="Player Name"
                  className="form-control mb-2"
                />
                <input
                  type="number"
                  name="bidAmount"
                  value={playerDetails.bidAmount}
                  onChange={handleInputChange}
                  placeholder="Bid Amount"
                  className="form-control mb-2"
                />
                <input
                  type="text"
                  name="photo"
                  value={playerDetails.photo}
                  onChange={handleInputChange}
                  placeholder="Player Photo URL"
                  className="form-control mb-2"
                />
                <button
                  className="btn btn-primary mt-2"
                  onClick={() => handleAddPlayer(auction._id)}
                >
                  Add Player
                </button>
              </div>
            </div>
          </div>
        ))
      ) : (
        <p className="text-center">No auctions found.</p>
      )}
    </div>
  );
}
