// src/components/PaymentPage.js
import React, { useState } from 'react';
import { loadStripe } from '@stripe/stripe-js';
import { Elements, CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import axios from 'axios';

// Load your Stripe public key
const stripePromise = loadStripe('pk_test_51QBtqRDVaxzZr2tTLGJoEtp6DLvscZPUmcUnE1ygGSieZQ3Dfn36HLZYY8uVmIAYJcvgewGetG8PxXpwpecNnyV200M4Kn2Jku');

// CheckoutForm component for the payment form
const CheckoutForm = () => {
    const [isProcessing, setIsProcessing] = useState(false);
    const [error, setError] = useState(null);
    const [succeeded, setSucceeded] = useState(false);
    const stripe = useStripe();
    const elements = useElements();

    const handleSubmit = async (event) => {
        event.preventDefault();
        setIsProcessing(true);
        setError(null);

        const { error, paymentIntent } = await stripe.confirmCardPayment(
            'client_secret_from_backend', // Get this from your backend
            {
                payment_method: {
                    card: elements.getElement(CardElement),
                },
            }
        );

        if (error) {
            setError(error.message);
        } else {
            if (paymentIntent.status === 'succeeded') {
                setSucceeded(true);
            }
        }
        setIsProcessing(false);
    };

    return (
        <div>
            <h2>Payment Page</h2>
            <form onSubmit={handleSubmit}>
                <CardElement />
                <button type="submit" disabled={!stripe || isProcessing || succeeded}>
                    {isProcessing ? 'Processing…' : 'Pay ₹2750'}
                </button>
            </form>
            {error && <div>{error}</div>}
            {succeeded && <div>Payment successful!</div>}
        </div>
    );
};

// PaymentPage Component to wrap the CheckoutForm with Elements
const PaymentPage = () => (
    <Elements stripe={stripePromise}>
        <CheckoutForm />
    </Elements>
);

export default PaymentPage;
