import React, { useState } from 'react';
import axios from 'axios';

export default function AuctionSetup() {
  const [tournamentDetails, setTournamentDetails] = useState({
    leaderEmail: '',
    tournamentName: '',
    tournamentDate: '',
    teamsCount: 0,
    totalPointsPerTeam: 0,
  });

  const [players, setPlayers] = useState([]);
  const [teams, setTeams] = useState([]);
  const [submitted, setSubmitted] = useState(false);

  // Handle tournament input changes
  const handleTournamentChange = (e) => {
    const { name, value } = e.target;
    setTournamentDetails({
      ...tournamentDetails,
      [name]: value,
    });
  };

  // Add a new player card
  const addPlayer = () => {
    setPlayers([...players, { name: '', profilePic: '', team: '', bidPoints: 0 }]);
  };

  // Update player details
  const handlePlayerChange = (index, e) => {
    const { name, value } = e.target;
    const updatedPlayers = [...players];
    updatedPlayers[index][name] = value;
    setPlayers(updatedPlayers);
  };

  // Update profile picture
  const handleProfilePicChange = (index, e) => {
    const file = e.target.files[0];
    const updatedPlayers = [...players];
    updatedPlayers[index].profilePic = URL.createObjectURL(file);
    setPlayers(updatedPlayers);
  };

  // Initialize teams with total points when form is submitted
  const handleSubmit = (e) => {
    e.preventDefault();
    const initialTeams = Array.from({ length: tournamentDetails.teamsCount }, (_, i) => ({
      teamName: `Team ${i + 1}`,
      totalPoints: tournamentDetails.totalPointsPerTeam,
      remainingPoints: tournamentDetails.totalPointsPerTeam,
    }));
    setTeams(initialTeams);
    setSubmitted(true);
  };

  // Handle team selection and bid points for each player
  const handleTeamSelection = (playerIndex, e) => {
    const { name, value } = e.target;
    const updatedPlayers = [...players];
    updatedPlayers[playerIndex][name] = value;
    setPlayers(updatedPlayers);

    if (name === 'bidPoints') {
      const selectedTeamIndex = teams.findIndex((team) => team.teamName === players[playerIndex].team);
      if (selectedTeamIndex >= 0) {
        const updatedTeams = [...teams];
        const bidPoints = parseInt(value) || 0;
        updatedTeams[selectedTeamIndex].remainingPoints =
          updatedTeams[selectedTeamIndex].totalPoints - bidPoints;
        setTeams(updatedTeams);
      }
    }
  };

  return (
    <div>
      <h1>Setup Auction</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          name="leaderEmail"
          value={tournamentDetails.leaderEmail}
          onChange={handleTournamentChange}
          placeholder="Leader Email"
          required
        />
        <input
          type="text"
          name="tournamentName"
          value={tournamentDetails.tournamentName}
          onChange={handleTournamentChange}
          placeholder="Tournament Name"
          required
        />
        <input
          type="date"
          name="tournamentDate"
          value={tournamentDetails.tournamentDate}
          onChange={handleTournamentChange}
          required
        />
        <input
          type="number"
          name="teamsCount"
          value={tournamentDetails.teamsCount}
          onChange={handleTournamentChange}
          placeholder="Number of Teams"
          required
        />
        <input
          type="number"
          name="totalPointsPerTeam"
          value={tournamentDetails.totalPointsPerTeam}
          onChange={handleTournamentChange}
          placeholder="Total Points Per Team"
          required
        />

        <h3>Players</h3>
        {players.map((player, index) => (
          <div key={index} className="player-card">
            <input
              type="text"
              name="name"
              value={player.name}
              onChange={(e) => handlePlayerChange(index, e)}
              placeholder="Player Name"
              required
            />
            <input
              type="file"
              onChange={(e) => handleProfilePicChange(index, e)}
              accept="image/*"
            />
            {player.profilePic && <img src={player.profilePic} alt="Player" width="50" />}
          </div>
        ))}
        <button type="button" onClick={addPlayer}>Add Player</button>
        <button type="submit">Submit</button>
      </form>

      {submitted && (
        <div>
          <h2>Teams and Points</h2>
          {teams.map((team, index) => (
            <div key={index} className="team-card">
              <h3>{team.teamName}</h3>
              <p>Total Points: {team.totalPoints}</p>
              <p>Remaining Points: {team.remainingPoints}</p>
            </div>
          ))}

          <h2>Player Assignment</h2>
          {players.map((player, index) => (
            <div key={index} className="player-card">
              <img src={player.profilePic} alt={player.name} width="50" />
              <h4>{player.name}</h4>
              <select
                name="team"
                value={player.team}
                onChange={(e) => handleTeamSelection(index, e)}
              >
                <option value="">Select Team</option>
                {teams.map((team, i) => (
                  <option key={i} value={team.teamName}>
                    {team.teamName}
                  </option>
                ))}
              </select>
              <input
                type="number"
                name="bidPoints"
                value={player.bidPoints}
                onChange={(e) => handleTeamSelection(index, e)}
                placeholder="Bid Points"
                required
              />
            </div>

          ))}
        </div>

      )}

    </div>
  );
}
