import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function ViewerDashboard() {
  const [auctions, setAuctions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!sessionStorage.getItem('hasVisited')) {
      sessionStorage.setItem('hasVisited', 'true');
      window.location.reload();
    }
  }, []);

  useEffect(() => {
    // API call to fetch auctions with the correct endpoint
    axios.get('http://localhost:5000/api/auctions/all')
      .then(response => {
        setAuctions(response.data); // Save auctions data to state
        setLoading(false);           // Set loading to false once data is fetched
      })
      .catch(err => {
        setError('Failed to fetch auctions'); // Error handling
        setLoading(false);
        console.error("Error fetching auctions:", err);  // Log the error for debugging
      });
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div>
      <h1>Viewer Dashboard</h1>
      {auctions.length > 0 ? (
        <div>
          {auctions.map((auction) => (
            <div key={auction._id} className="auction-card">
              <h2>{auction.tournamentName}</h2>
              <p>Leader Email: {auction.leaderEmail}</p>
              <p>Total Points per Team: {auction.bidPointsPerTeam}</p>

              <h3>Teams</h3>
              {auction.teams && auction.teams.length > 0 ? (
                <ul>
                  {auction.teams.map((team) => (
                    <li key={team._id}>
                      <p>Team Name: {team.name}</p>
                      <p>Bid Points: {team.bidPoints}</p>
                      <p>Remaining Points: {team.remainingBidPoints}</p>
                    </li>
                  ))}
                </ul>
              ) : (
                <p>No teams available for this auction.</p>
              )}

              <h3>Players</h3>
              {auction.players && auction.players.length > 0 ? (
                <ul>
                  {auction.players.map((playerId) => (
                    <li key={playerId}>
                      <p>Player ID: {playerId}</p>
                      {/* Here you could fetch additional details if necessary */}
                    </li>
                  ))}
                </ul>
              ) : (
                <p>No players added to this auction yet.</p>
              )}
            </div>
          ))}
        </div>
      ) : (
        <p>No auctions found.</p>
      )}
    </div>
  );
}
