import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { FaUser, FaSignInAlt, FaInfoCircle, FaTrophy, FaTimes } from 'react-icons/fa';
import { registerUser } from '../api';

const LandingPage = () => {
  const [showSignup, setShowSignup] = useState(false);
  const [formData, setFormData] = useState({ email: '', password: '', confirmPassword: '' });
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleGetStarted = () => {
    setShowSignup(true);
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    // Check if passwords match
    if (formData.password !== formData.confirmPassword) {
      setError('Passwords do not match');
      return;
    }

    try {
      await registerUser({ email: formData.email, password: formData.password, role: 'viewer' });
      navigate('/viewer-dashboard');
    } catch (error) {
      console.error("Registration failed", error);
      setError('Registration failed. Please try again.');
    }
  };

  return (
    <div className="min-vh-100 d-flex flex-column position-relative" style={{ backgroundColor: '#99EDC3' }}>
      {/* Signup Modal */}
      {showSignup && (
        <div className="position-fixed top-0 start-0 w-100 h-100 d-flex justify-content-center align-items-center" style={{ backgroundColor: 'rgba(0,0,0,0.5)', zIndex: 1000 }}>
          <div className="card" style={{ maxWidth: '400px', width: '90%' }}>
            <div className="card-body" style={{ background: 'rgba(255, 255, 255, 0.9)' }}>
              <div className="d-flex justify-content-between align-items-center mb-4">
                <h3 className="card-title m-0">Sign Up</h3>
                <button
                  className="btn btn-link text-dark p-0"
                  onClick={() => setShowSignup(false)}
                  style={{ fontSize: '1.5rem' }}
                >
                  <FaTimes />
                </button>
              </div>
              <form onSubmit={handleSubmit}>
                <div className="mb-3">
                  <label className="form-label">Full Name</label>
                  <input 
                    type="text" 
                    className="form-control" 
                    name="fullName"
                    placeholder="Enter your full name"
                    onChange={handleChange}
                    required
                  />
                </div>
                <div className="mb-3">
                  <label className="form-label">Email</label>
                  <input 
                    type="email" 
                    className="form-control" 
                    name="email"
                    placeholder="Enter your email"
                    onChange={handleChange}
                    required
                  />
                </div>
                <div className="mb-3">
                  <label className="form-label">Password</label>
                  <input 
                    type="password" 
                    className="form-control" 
                    name="password"
                    placeholder="Create a password"
                    onChange={handleChange}
                    required
                  />
                </div>
                <div className="mb-3">
                  <label className="form-label">Confirm Password</label>
                  <input 
                    type="password" 
                    className="form-control" 
                    name="confirmPassword"
                    placeholder="Confirm your password"
                    onChange={handleChange}
                    required
                  />
                </div>
                {error && <div className="alert alert-danger">{error}</div>}
                <button 
                  type="submit" 
                  className="btn w-100" 
                  style={{ backgroundColor: '#040c0e', color: 'white' }}
                >
                  Create Account
                </button>
                <div className="text-center mt-3">
                  <small className="text-muted">
                    Already have an account? <Link to="/login" className="text-decoration-none">Login</Link>
                  </small>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}

      {/* Rest of your existing landing page code */}
      <div className="container my-5">
        <div className="row align-items-center">
          <div className="col-md-6">
            <h1 className="display-4 fw-bold mb-3">
              We create solutions for your cricket auctions
            </h1>
            <p className="text-dark mb-4">
              Our platform provides a seamless and streamlined solution for organizing and participating in cricket auctions, making team building easier than ever.
            </p>
            <button
              className="btn"
              style={{ backgroundColor: '#040c0e', color: 'white' }}
              onClick={handleGetStarted}
            >
              Get Started
            </button>
          </div>
          <div className="col-md-6 text-center">
            <img
              src="../images/player-removebg-preview.png"
              alt="Hero illustration"
              className="img-fluid rounded"
              style={{ maxWidth: '70%' }}
            />
          </div>
        </div>
      </div>

      {/* Services Section */}
      <div className="container my-5">
        <h2 className="text-center fw-bold mb-4">We Provide The Best Services</h2>
        <div className="row g-4">
          {[
            { icon: <FaUser style={{ color: '#049c0e' }} />, title: "User Management", desc: "Complete player and team registration system" },
            { icon: <FaTrophy style={{ color: '#049c0e' }} />, title: "Live Auctions", desc: "Real-time bidding and team management" },
            { icon: <FaInfoCircle style={{ color: '#049c0e' }} />, title: "Analytics", desc: "Comprehensive player and team statistics" },
            { icon: <FaSignInAlt style={{ color: '#049c0e' }} />, title: "Tournament", desc: "Complete tournament organization tools" },
          ].map((service, i) => (
            <div key={i} className="col-md-6 col-lg-3">
              <div className="card shadow-sm" style={{
                background: 'rgba(0, 255, 0, 0.2)',
                backdropFilter: 'blur(10px)',
                borderRadius: '8px',
              }}>
                <div className="card-body text-center">
                  <div
                    className="bg-dark text-white rounded-circle d-flex justify-content-center align-items-center mx-auto mb-3"
                    style={{ width: '50px', height: '50px', backgroundColor: '#0f6efd' }}
                  >
                    {service.icon}
                  </div>
                  <h5 className="card-title">{service.title}</h5>
                  <p className="card-text text-dark">{service.desc}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Keep the rest of your existing sections here */}
    </div>
  );
};

export default LandingPage;
