import React, { useState, useEffect } from 'react';

const AdminDashboard = () => {
    const [leaders, setLeaders] = useState([]);
    const [users, setUsers] = useState([]);
    const [packages, setPackages] = useState([]);
    const [totalUsers, setTotalUsers] = useState(0);
    const [totalLeaders, setTotalLeaders] = useState(0);
    const [newPackage, setNewPackage] = useState({ name: '', price: '', duration: '' });
    const [editingPackage, setEditingPackage] = useState(null);

    // Fetch all data when the component mounts
    useEffect(() => {
        const fetchData = async () => {
            try {
                // Fetch leaders, users, and stats
                const leadersResponse = await fetch('http://localhost:5000/api/admin/leaders');
                const leadersData = await leadersResponse.json();

                const usersResponse = await fetch('/api/admin/users');
                const usersData = await usersResponse.json();

                const packagesResponse = await fetch('/api/admin/packages');
                const packagesData = await packagesResponse.json();

                const statsResponse = await fetch('/api/admin/stats');
                const statsData = await statsResponse.json();

                setLeaders(leadersData);
                setUsers(usersData);
                setPackages(packagesData);
                setTotalUsers(statsData.totalUsers);
                setTotalLeaders(statsData.totalLeaders);
            } catch (error) {
                console.error("Error fetching data:", error);
            }
        };
        fetchData();
    }, []);

    // Add a new package
const handleAddPackage = async () => {
    try {
        const response = await fetch('/api/admin/packages', { // Change to match the route defined
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(newPackage),
        });
        if (response.ok) {
            const packageData = await response.json(); // Ensure you get the response data
            setPackages([...packages, packageData]); // Use the newly created package
            setNewPackage({ name: '', price: '', duration: '' });
            alert('Package added successfully');
        } else {
            alert('Failed to add package');
        }
    } catch (error) {
        console.error("Error adding package:", error);
    }
};

    // Edit an existing package
    const handleEditPackage = async (packageId) => {
        try {
            const response = await fetch(`/api/admin/edit-package/${packageId}`, {
                method: 'PUT',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(editingPackage),
            });
            if (response.ok) {
                alert('Package updated successfully');
                setPackages(packages.map(pkg => pkg._id === packageId ? editingPackage : pkg));
                setEditingPackage(null);
            } else {
                alert('Failed to update package');
            }
        } catch (error) {
            console.error("Error editing package:", error);
        }
    };

    return (
        <div className="dashboard">
            <h2>Admin Dashboard</h2>
            <div className="stats">
                <p>Total Users: {totalUsers}</p>
                <p>Total Leaders: {totalLeaders}</p>
            </div>

            <h3>Users and Leaders Details</h3>
            <table>
                <thead>
                    <tr>
                        <th>Email</th>
                        <th>Role</th>
                        <th>Subscription</th>
                    </tr>
                </thead>
                <tbody>
                    {[...users, ...leaders].map(user => (
                        <tr key={user._id}>
                            <td>{user.email}</td>
                            <td>{user.role}</td>
                            <td>{user.subscription}</td>
                        </tr>
                    ))}
                </tbody>
            </table>

            <h3>Manage Packages</h3>
            <div className="package-management">
                <h4>Add Package</h4>
                <input
                    type="text"
                    placeholder="Package Name"
                    value={newPackage.name}
                    onChange={(e) => setNewPackage({ ...newPackage, name: e.target.value })}
                />
                <input
                    type="number"
                    placeholder="Price"
                    value={newPackage.price}
                    onChange={(e) => setNewPackage({ ...newPackage, price: e.target.value })}
                />
                <input
                    type="number"
                    placeholder="Duration (months)"
                    value={newPackage.duration}
                    onChange={(e) => setNewPackage({ ...newPackage, duration: e.target.value })}
                />
                <button onClick={handleAddPackage}>Add Package</button>

                <h4>Existing Packages</h4>
                <table>
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Price</th>
                            <th>Duration</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {packages.map(pkg => (
                            <tr key={pkg._id}>
                                <td>
                                    {editingPackage && editingPackage._id === pkg._id ? (
                                        <input
                                            type="text"
                                            value={editingPackage.name}
                                            onChange={(e) => setEditingPackage({ ...editingPackage, name: e.target.value })}
                                        />
                                    ) : pkg.name}
                                </td>
                                <td>
                                    {editingPackage && editingPackage._id === pkg._id ? (
                                        <input
                                            type="number"
                                            value={editingPackage.price}
                                            onChange={(e) => setEditingPackage({ ...editingPackage, price: e.target.value })}
                                        />
                                    ) : pkg.price}
                                </td>
                                <td>
                                    {editingPackage && editingPackage._id === pkg._id ? (
                                        <input
                                            type="number"
                                            value={editingPackage.duration}
                                            onChange={(e) => setEditingPackage({ ...editingPackage, duration: e.target.value })}
                                        />
                                    ) : pkg.duration}
                                </td>
                                <td>
                                    {editingPackage && editingPackage._id === pkg._id ? (
                                        <button onClick={() => handleEditPackage(pkg._id)}>Save</button>
                                    ) : (
                                        <button onClick={() => setEditingPackage(pkg)}>Edit</button>
                                    )}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default AdminDashboard;
