import React from 'react';
import { Navbar, Nav, Container, Button } from 'react-bootstrap';
import { Link, useNavigate } from 'react-router-dom';
import { FaSignOutAlt, FaHome, FaUser, FaShieldAlt, FaInfoCircle, FaQuestionCircle, FaEnvelope } from 'react-icons/fa';

const CustomNavbar = () => {
    const navigate = useNavigate();

    // Get the user role and email from local storage
    const userRole = localStorage.getItem('role');
    const userEmail = localStorage.getItem('userEmail');

    // Handle logout
    const handleLogout = () => {
        // Clear user information from local storage
        localStorage.removeItem('userEmail');
        localStorage.removeItem('role');

        // Redirect to the home page
        navigate('/');
    };

    // Handle scrolling to the sections
    const scrollToSection = (sectionId) => {
        const section = document.getElementById(sectionId);
        if (section) {
            section.scrollIntoView({ behavior: 'smooth' });
        }
    };

    return (
        <Navbar bg="light" expand="lg">
            <Container>
                <Navbar.Brand as={Link} to="/">
                    <img
                        src="/images/logo8.png"
                        alt="CrickBidders Logo"
                        height="30"
                        className="d-inline-block align-top"
                    />
                </Navbar.Brand>
                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav className="me-auto">
                        <Nav.Link as={Link} to="/"><FaHome /> Home</Nav.Link>

                        {(userRole === 'leader' || userRole === 'admin') && (
                            <Nav.Link as={Link} to="/payment"><FaUser /> Leader</Nav.Link>
                        )}

                        {userRole === 'admin' && (
                            <Nav.Link as={Link} to="/admin-dashboard"><FaShieldAlt /> Admin</Nav.Link>
                        )}

                        <Nav.Link onClick={() => scrollToSection('about')}><FaInfoCircle /> About</Nav.Link>
                        <Nav.Link onClick={() => scrollToSection('how-to-use')}><FaQuestionCircle /> How</Nav.Link>
                        <Nav.Link onClick={() => scrollToSection('contact')}><FaEnvelope /> Contact Us</Nav.Link>
                    </Nav>
                    <Nav>
                        {userEmail && userRole ? (
                            <Button variant="outline-primary" onClick={handleLogout}>
                                <FaSignOutAlt /> Logout
                            </Button>
                        ) : (
                            <Nav.Link as={Link} to="/login">Log In</Nav.Link>
                        )}
                    </Nav>
                </Navbar.Collapse>
            </Container>
        </Navbar>
    );
};

export default CustomNavbar;
