import React from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Navbar = () => {
    const navigate = useNavigate();

    // Get the user role and email from local storage
    const userRole = localStorage.getItem('role');
    const userEmail = localStorage.getItem('userEmail');

    // Handle logout
    const handleLogout = () => {
        // Clear user information from local storage
        localStorage.removeItem('userEmail');
        localStorage.removeItem('role');

        // Redirect to the home page
        navigate('/');
    };

    // Handle scrolling to the sections
    const scrollToSection = (sectionId) => {
        const section = document.getElementById(sectionId);
        if (section) {
            section.scrollIntoView({ behavior: 'smooth' });
        }
    };

    return (
        <header className="navbar" style={{ background: 'linear-gradient(to right, #4dc3c7, #48da96)', color: '#ffffff' }}>
            <h2 className="logo" style={{ color: '#ffffff' }}>CrickBidders</h2>
            <nav className="nav-links">
                <Link to="/" className="nav-link" style={{ color: '#ffffff', textDecoration: 'none' }}>Home</Link>

                {/* Show the Leader link if the user role is either "leader" or "admin" */}
                {(userRole === 'leader' || userRole === 'admin') && (
                    <Link to="/payment" className="nav-link" style={{ color: '#ffffff', textDecoration: 'none' }}>Leader</Link>
                )}

                {/* Show the Admin link only if the user role is "admin" */}
                {userRole === 'admin' && (
                    <Link to="/admin-dashboard" className="nav-link" style={{ color: '#ffffff', textDecoration: 'none' }}>Admin</Link>
                )}

                {/* Show "Logout" if userEmail and role exist, otherwise show Log In */}
                {userEmail && userRole ? (
                    <button onClick={handleLogout} className="nav-link logout-button" 
                        style={{ color: '#ffffff', background: 'none', border: 'none', cursor: 'pointer', textDecoration: 'none' }}>
                        Logout
                    </button>
                ) : (
                    <>
                        <Link to="/login" className="nav-link" style={{ color: '#ffffff', textDecoration: 'none' }}>Log In</Link>
                    </>
                )}

                <button onClick={() => scrollToSection('about')} className="nav-link" 
                    style={{ color: '#ffffff', background: 'none', border: 'none', cursor: 'pointer', textDecoration: 'none' }}>
                    About
                </button> {/* Scroll to About Section */}
                <button onClick={() => scrollToSection('how-to-use')} className="nav-link" 
                    style={{ color: '#ffffff', background: 'none', border: 'none', cursor: 'pointer', textDecoration: 'none' }}>
                    How
                </button> {/* Scroll to How Section */}
                <button onClick={() => scrollToSection('contact')} className="nav-link" 
                    style={{ color: '#ffffff', background: 'none', border: 'none', cursor: 'pointer', textDecoration: 'none' }}>
                    Contact Us
                </button> {/* Scroll to Contact Section */}

            </nav>
        </header>
    );
};

export default Navbar;
