// src/components/AdminDashboard.js

import React, { useState, useEffect } from 'react';

const AdminDashboard = () => {
    const [leaders, setLeaders] = useState([]);
    const [totalUsers, setTotalUsers] = useState(0);
    const [totalLeaders, setTotalLeaders] = useState(0);

    // Fetch the list of leaders and stats when component loads
    useEffect(() => {
        const fetchData = async () => {
            try {
                // Fetch leaders list and count of users and leaders
                const leadersResponse = await fetch('/api/admin/leaders');
                const leadersData = await leadersResponse.json();

                const statsResponse = await fetch('/api/admin/stats');
                const statsData = await statsResponse.json();

                setLeaders(leadersData);
                setTotalUsers(statsData.totalUsers);
                setTotalLeaders(statsData.totalLeaders);
            } catch (error) {
                console.error("Error fetching data:", error);
            }
        };
        fetchData();
    }, []);

    const handleCancelSubscription = async (leaderId) => {
        try {
            // Send request to cancel subscription
            const response = await fetch(`/api/admin/cancel-subscription/${leaderId}`, {
                method: 'POST',
            });

            if (response.ok) {
                alert('Subscription canceled successfully');
                setLeaders(leaders.filter(leader => leader._id !== leaderId));
            } else {
                alert('Failed to cancel subscription');
            }
        } catch (error) {
            console.error("Error canceling subscription:", error);
        }
    };

    return (
        <div className="dashboard">
            <h2>Admin Dashboard</h2>
            <div className="stats">
                <p>Total Users: {totalUsers}</p>
                <p>Total Leaders: {totalLeaders}</p>
            </div>

            <h3>Leaders and Subscriptions</h3>
            <table>
                <thead>
                    <tr>
                        <th>Email</th>
                        <th>Package</th>
                        <th>Expiry Date</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {leaders.map(leader => (
                        <tr key={leader._id}>
                            <td>{leader.email}</td>
                            <td>{leader.package}</td>
                            <td>{new Date(leader.expiryDate).toLocaleDateString()}</td>
                            <td>
                                <button onClick={() => handleCancelSubscription(leader._id)}>
                                    Cancel Subscription
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default AdminDashboard;
