import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function ViewerDashboard() {
  const [auctions, setAuctions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');
  const [formStatus, setFormStatus] = useState('');

  // Refresh page on mount (if necessary)
  useEffect(() => {
    if (!sessionStorage.getItem('hasVisited')) {
      sessionStorage.setItem('hasVisited', 'true');
      window.location.reload();
    }
  }, []);

  useEffect(() => {
    // Fetch auctions data from the backend
    axios.get('http://localhost:5000/api/auctions')
      .then(response => {
        setAuctions(response.data); // Set auctions data
        setLoading(false);           // Stop loading after data is fetched
      })
      .catch(err => {
        setError('Failed to fetch auctions'); // Set error message
        setLoading(false);
      });
  }, []);

  const handleSubmitContactForm = async (e) => {
    e.preventDefault();

    // Validate input data
    if (!name || !email || !message) {
      setFormStatus('Please fill in all the fields.');
      return;
    }

    try {
      const response = await axios.post('http://localhost:5000/api/contact', {
        name,
        email,
        message,
      });

      if (response.status === 201) {
        setFormStatus('Message received successfully!');
        setName('');
        setEmail('');
        setMessage('');
      }
    } catch (error) {
      setFormStatus('Error sending message. Please try again.');
    }
  };

  if (loading) {
    return <div>Loading auctions...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div>
      {/* Contact Form */}
      <div>
        <h1>Contact Us</h1>
        <form onSubmit={handleSubmitContactForm}>
          <div>
            <label htmlFor="name">Name:</label>
            <input 
              type="text" 
              id="name" 
              value={name} 
              onChange={(e) => setName(e.target.value)} 
              required 
            />
          </div>
          <div>
            <label htmlFor="email">Email:</label>
            <input 
              type="email" 
              id="email" 
              value={email} 
              onChange={(e) => setEmail(e.target.value)} 
              required 
            />
          </div>
          <div>
            <label htmlFor="message">Message:</label>
            <textarea 
              id="message" 
              value={message} 
              onChange={(e) => setMessage(e.target.value)} 
              required 
            />
          </div>
          <button type="submit">Submit</button>
        </form>
        {formStatus && <p>{formStatus}</p>}
      </div>

      {/* Auctions Viewer */}
      <h2>Live Auctions</h2>
      {auctions.length > 0 ? (
        <div>
          {auctions.map((auction) => (
            <div key={auction._id}>
              <h2>{auction.tournamentName}</h2>
              <p>Date: {new Date(auction.tournamentDate).toLocaleDateString()}</p>
              <p>Teams: {auction.teamsCount}</p>
              <p>Total Points per Team: {auction.totalPointsPerTeam}</p>
              <p>Status: {auction.isLive ? 'Live' : 'Completed'}</p>
              {auction.players.length > 0 && (
                <div>
                  <h3>Players</h3>
                  <ul>
                    {auction.players.map((player) => (
                      <li key={player._id}>
                        <img src={player.profilePic} alt={player.name} width="50" />
                        <p>{player.name}</p>
                        <p>Team: {player.team}</p>
                        <p>Bid Points: {player.bidPoints}</p>
                      </li>
                    ))}
                  </ul>
                </div>
              )}
            </div>
          ))}
        </div>
      ) : (
        <p>No auctions found.</p>
      )}
    </div>
  );
}
