import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function ViewerDashboard() {
  const [auctions, setAuctions] = useState([]); // State to hold the auctions data
  const [loading, setLoading] = useState(true); // State to handle loading
  const [error, setError] = useState(null); // State for error handling

  useEffect(() => {
    // Prevent page reload on first visit
    if (!sessionStorage.getItem('hasVisited')) {
      sessionStorage.setItem('hasVisited', 'true');
      window.location.reload();
    }
  }, []);
  useEffect(() => {
    console.log("Fetched data:", response.data); // Check response
    setAuctions(response.data); 
  }, []);

  useEffect(() => {
    console.log("Auctions state updated:", auctions);
  }, [auctions]); // This will trigger every time `auctions` changes

  useEffect(() => {
    // Fetch auctions from the API endpoint
    axios
      .get('http://localhost:5000/api/auctions/') // Connect to the API endpoint
      .then((response) => {
        setAuctions(response.data); // Store auction data in state
        setLoading(false); // Set loading to false when data is fetched
      })
      .catch((err) => {
        setError('Failed to fetch auctions'); // Handle errors
        setLoading(false);
        console.error('Error fetching auctions:', err); // Log the error for debugging
      });
  }, [auctions]); // Empty dependency array means this effect runs once when the component mounts

  if (loading) {
    return <div>Loading...</div>; // Show loading message while data is being fetched
  }

  if (error) {
    return <div>{error}</div>; // Display error message if fetching fails
  }

  return (
    <div>
      {auctions.length > 0 ? (
        auctions.map(auction => (
          <div key={auction._id}>
            <h2>{auction.tournamentName || "No Tournament Name"}</h2>
          </div>
        ))
      ) : (
        <p>No auctions found.</p>
      )}
    </div>
  );
};
