import React from 'react';
import { Link } from 'react-router-dom';

const AdminSidebar = ({ toggleSidebar }) => {
    // Function to handle link clicks and hide the sidebar
    const handleLinkClick = () => {
        // Hide the sidebar when any link is clicked
        toggleSidebar(false);
    };

    return (
        <aside className="admin-sidebar bg-light p-4 border-end" style={{ width: '250px', height: '100vh' }}>
            <h3 className="sidebar-title text-primary mb-4">Admin Dashboard</h3>
            <nav className="sidebar-nav">
                <ul className="nav flex-column">
                    <li className="nav-item">
                        <Link to="/admin-dashboard" className="nav-link sidebar-link text-dark" onClick={handleLinkClick}>
                            Dashboard Overview
                        </Link>
                    </li>
                    <li className="nav-item">
                        <Link to="/admin-users" className="nav-link sidebar-link text-dark" onClick={handleLinkClick}>
                            Users
                        </Link>
                    </li>
                    <li className="nav-item">
                        <Link to="/admin-auctions" className="nav-link sidebar-link text-dark" onClick={handleLinkClick}>
                            Manage Auctions
                        </Link>
                    </li>
                    <li className="nav-item">
                        <Link to="/admin-reports" className="nav-link sidebar-link text-dark" onClick={handleLinkClick}>
                            Reports
                        </Link>
                    </li>
                    <li className="nav-item">
                        <Link to="/admin-settings" className="nav-link sidebar-link text-dark" onClick={handleLinkClick}>
                            Settings
                        </Link>
                    </li>
                </ul>
            </nav>
        </aside>
    );
};

export default AdminSidebar;
