import React, { useState } from 'react';

export default function LeaderDashboard() {
  const [formData, setFormData] = useState({
    leaderEmail: '',
    tournamentName: '',
    teamsCount: 0,
    bidPointsPerTeam: 0,
    teams: [],
    players: [], // Array for player profiles
  });

  const [submittedData, setSubmittedData] = useState(null); // To hold the submitted data

  // Handle input change
  const handleChange = (e) => {
    const { name, value } = e.target;
    const newValue = name === 'teamsCount' || name === 'bidPointsPerTeam' ? parseInt(value) : value;

    setFormData((prevData) => {
      // Update teams array based on teamsCount or bidPointsPerTeam
      if (name === 'teamsCount') {
        const updatedTeams = Array.from(
          { length: newValue || 0 },
          (_, index) => ({
            name: prevData.teams[index]?.name || '',
            bidPoints: prevData.bidPointsPerTeam || 0,
          })
        );
        return {
          ...prevData,
          teamsCount: newValue,
          teams: updatedTeams,
        };
      }

      if (name === 'bidPointsPerTeam') {
        const updatedTeams = prevData.teams.map((team) => ({
          ...team,
          bidPoints: newValue || 0,
        }));
        return {
          ...prevData,
          bidPointsPerTeam: newValue,
          teams: updatedTeams,
        };
      }

      return {
        ...prevData,
        [name]: newValue,
      };
    });
  };

  // Handle team names input change
  const handleTeamChange = (index, value) => {
    const updatedTeams = [...formData.teams];
    updatedTeams[index] = {
      ...updatedTeams[index],
      name: value,
    };
    setFormData({
      ...formData,
      teams: updatedTeams,
    });
  };

  // Handle player input change
  const handlePlayerChange = (index, field, value) => {
    const updatedPlayers = [...formData.players];
    updatedPlayers[index] = {
      ...updatedPlayers[index],
      [field]: value,
    };
    setFormData({
      ...formData,
      players: updatedPlayers,
    });
  };

  // Add new player profile
  const addPlayer = () => {
    setFormData({
      ...formData,
      players: [...formData.players, { name: '', photo: null }],
    });
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    setSubmittedData(formData); // Store submitted data
  };

  return (
    <div>
      <h1>Leader Dashboard</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          name="leaderEmail"
          value={formData.leaderEmail}
          onChange={handleChange}
          placeholder="Leader Email"
          required
        />
        <input
          type="text"
          name="tournamentName"
          value={formData.tournamentName}
          onChange={handleChange}
          placeholder="Tournament Name"
          required
        />
        <input
          type="number"
          name="teamsCount"
          value={formData.teamsCount}
          onChange={handleChange}
          placeholder="Number of Teams"
          required
        />
        <input
          type="number"
          name="bidPointsPerTeam"
          value={formData.bidPointsPerTeam}
          onChange={handleChange}
          placeholder="Bid Points Per Team"
          required
        />

        {/* Render Team Names dynamically with Bid Points */}
        <div>
          <h3>Team Names</h3>
          {formData.teams.map((team, index) => (
            <div key={index}>
              <input
                type="text"
                value={team.name}
                onChange={(e) => handleTeamChange(index, e.target.value)}
                placeholder={`Team ${index + 1} Name`}
                required
              />
              <span> - Bid Points: {team.bidPoints}</span>
            </div>
          ))}
        </div>

        {/* Render Player Profiles */}
        <div>
          <h3>Players</h3>
          {formData.players.map((player, index) => (
            <div key={index} style={{ marginBottom: '10px' }}>
              <input
                type="text"
                value={player.name}
                onChange={(e) => handlePlayerChange(index, 'name', e.target.value)}
                placeholder={`Player ${index + 1} Name`}
                required
              />
              <input
                type="file"
                accept="image/*"
                onChange={(e) => handlePlayerChange(index, 'photo', e.target.files[0])}
              />
              <div>
                <p>Name: {player.name}</p>
                {player.photo ? (
                  <img
                    src={URL.createObjectURL(player.photo)}
                    alt={`Player ${index + 1} Photo`}
                    style={{ width: '100px', height: '100px', borderRadius: '50%' }}
                  />
                ) : (
                  <p>No Photo Selected</p>
                )}
              </div>
            </div>
          ))}
          <button type="button" onClick={addPlayer}>Add Player</button>
        </div>

        <button type="submit">Submit Tournament</button>
      </form>

      {/* Display Submitted Data */}
      {submittedData && (
        <div>
          <h2>Tournament Information</h2>
          <h3>Tournament: {submittedData.tournamentName}</h3>
          <h4>Teams</h4>
          {submittedData.teams.map((team, index) => (
            <div key={index}>
              <h5>{team.name}</h5>
              <p>Bid Points: {team.bidPoints}</p>
            </div>
          ))}

          <h4>Players</h4>
          {submittedData.players.map((player, index) => (
            <div key={index} style={{ marginBottom: '10px' }}>
              <p>Name: {player.name}</p>
              {player.photo ? (
                <img
                  src={URL.createObjectURL(player.photo)}
                  alt={`Player ${index + 1} Photo`}
                  style={{ width: '100px', height: '100px', borderRadius: '50%' }}
                />
              ) : (
                <p>No Photo Selected</p>
              )}
              {/* Team selection and bidding */}
              <div>
                <select>
                  {submittedData.teams.map((team, idx) => (
                    <option key={idx} value={team.name}>
                      {team.name}
                    </option>
                  ))}
                </select>
                <input type="number" placeholder="Bid Amount" />
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
