// Login.js
import React from 'react';

const Login = ({ onClose }) => {
  return (
    <div className="d-flex justify-content-center align-items-center flex-column" style={{ minHeight: '300px' }}>
      <h2 className="text-center mb-4">Login</h2>
      <form style={{ width: '100%' }}>
        <div className="mb-3">
          <input 
            type="email" 
            className="form-control" 
            placeholder="Email" 
            style={{ borderColor: '#040c0e' }} 
          />
        </div>
        <div className="mb-3">
          <input 
            type="password" 
            className="form-control" 
            placeholder="Password" 
            style={{ borderColor: '#040c0e' }} 
          />
        </div>
        <button type="submit" className="btn" style={{ backgroundColor: '#040c0e', color: 'white', width: '100%' }}>
          Login
        </button>
      </form>
      <div className="text-center mt-3">
        <button className="btn btn-link" onClick={onClose}>Close</button>
      </div>
    </div>
  );
};

export default Login;
