import React from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Navbar = () => {
    const navigate = useNavigate();

    // Get the user role and email from local storage
    const userRole = localStorage.getItem('role');
    const userEmail = localStorage.getItem('userEmail');

    // Handle logout
    const handleLogout = () => {
        // Clear user information from local storage
        localStorage.removeItem('userEmail');
        localStorage.removeItem('role');

        // Redirect to the home page
        navigate('/');
    };

    // Handle scrolling to the sections
    const scrollToSection = (sectionId) => {
        const section = document.getElementById(sectionId);
        if (section) {
            section.scrollIntoView({ behavior: 'smooth' });
        }
    };

    return (
        <header style={{
            display: 'flex',
            justifyContent: 'space-between',
            padding: '0.1rem',
            background: 'linear-gradient(to right, #4dc3c7, #48da96)',
            color: '#ffffff',
            alignItems: 'center',
            zIndex: 1000
        }}>
            <h2 style={{ margin: 0 }}>CrickBidders</h2>
            <nav style={{ display: 'flex', gap: '1rem' }}>
                <Link to="/" style={{ color: '#ffffff', textDecoration: 'none' }}>Home</Link>

                {/* Show the Leader link if the user role is either "leader" or "admin" */}
                {(userRole === 'leader' || userRole === 'admin') && (
                    <Link to="/leader-dashboard" style={{ color: '#ffffff', textDecoration: 'none' }}>Leader</Link>
                )}

                {/* Show the Admin link only if the user role is "admin" */}
                {userRole === 'admin' && (
                    <Link to="/admin-dashboard" style={{ color: '#ffffff', textDecoration: 'none' }}>Admin</Link>
                )}

                {/* Show "Logout" if userEmail and role exist, otherwise show Sign Up and Log In */}
                {userEmail && userRole ? (
                    <button onClick={handleLogout} style={{ color: '#ffffff', background: 'none', border: 'none', cursor: 'pointer' }}>Logout</button>
                ) : (
                    <Link to="/login" style={{ color: '#ffffff', textDecoration: 'none' }}>Log In</Link>
                )}

                <button onClick={() => scrollToSection('about')} style={{ color: '#ffffff', background: 'none', border: 'none', cursor: 'pointer' }}>About</button>
                <button onClick={() => scrollToSection('how-to-use')} style={{ color: '#ffffff', background: 'none', border: 'none', cursor: 'pointer' }}>How</button>
                <button onClick={() => scrollToSection('contact')} style={{ color: '#ffffff', background: 'none', border: 'none', cursor: 'pointer' }}>Contact Us</button>
            </nav>
        </header>
    );
};

export default Navbar;
