import React, { useState } from 'react';
import axios from 'axios';

export default function LeaderDashboard() {
  const [formData, setFormData] = useState({
    leaderEmail: '',
    tournamentName: '',
    teamsCount: 0,
    bidPointsPerTeam: 0,
    teams: [],
    players: [],
  });

  const [submittedData, setSubmittedData] = useState(null); // To hold the submitted data

  const handleChange = (e) => {
    const { name, value } = e.target;
    const newValue = name === 'teamsCount' || name === 'bidPointsPerTeam' ? parseInt(value) : value;

    setFormData((prevData) => {
      if (name === 'teamsCount') {
        const updatedTeams = Array.from({ length: newValue || 0 }, (_, index) => ({
          name: prevData.teams[index]?.name || '',
          bidPoints: prevData.bidPointsPerTeam || 0,
          remainingBidPoints: prevData.bidPointsPerTeam || 0,
        }));
        return {
          ...prevData,
          teamsCount: newValue,
          teams: updatedTeams,
        };
      }

      if (name === 'bidPointsPerTeam') {
        const updatedTeams = prevData.teams.map((team) => ({
          ...team,
          bidPoints: newValue || 0,
          remainingBidPoints: newValue || 0,
        }));
        return {
          ...prevData,
          bidPointsPerTeam: newValue,
          teams: updatedTeams,
        };
      }

      return {
        ...prevData,
        [name]: newValue,
      };
    });
  };

  const handleTeamChange = (index, value) => {
    const updatedTeams = [...formData.teams];
    updatedTeams[index] = {
      ...updatedTeams[index],
      name: value,
    };
    setFormData({
      ...formData,
      teams: updatedTeams,
    });
  };

  const handlePlayerChange = (index, field, value) => {
    const updatedPlayers = [...formData.players];
    updatedPlayers[index] = {
      ...updatedPlayers[index],
      [field]: value,
    };
    setFormData({
      ...formData,
      players: updatedPlayers,
    });
  };

  const addPlayer = () => {
    setFormData({
      ...formData,
      players: [...formData.players, { name: '', photo: null, bidAmount: 0, team: '', done: false }],
    });
  };

  const handleDone = (index) => {
    const updatedPlayers = [...formData.players];
    updatedPlayers[index].done = true;
    setFormData({
      ...formData,
      players: updatedPlayers,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Send the form data to the backend API to create the auction
      const response = await axios.post('http://localhost:5000/api/auctions/create', formData);

      // Assuming the backend returns the 'auction' object in the response
      const { auction } = response.data;

      // Update the frontend with the auction data
      setSubmittedData(auction);

      // Reset the form data after submission (optional)
      setFormData({
        leaderEmail: '',
        tournamentName: '',
        teamsCount: 0,
        bidPointsPerTeam: 0,
        teams: [],
        players: [],
      });
    } catch (error) {
      console.error('Error submitting tournament:', error);
      alert('There was an error submitting the tournament.');
    }
  };

  return (
    <div>
      <h1>Leader Dashboard</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          name="leaderEmail"
          value={formData.leaderEmail}
          onChange={handleChange}
          placeholder="Leader Email"
          required
        />
        <input
          type="text"
          name="tournamentName"
          value={formData.tournamentName}
          onChange={handleChange}
          placeholder="Tournament Name"
          required
        />
        <input
          type="number"
          name="teamsCount"
          value={formData.teamsCount}
          onChange={handleChange}
          placeholder="Number of Teams"
          required
        />
        <input
          type="number"
          name="bidPointsPerTeam"
          value={formData.bidPointsPerTeam}
          onChange={handleChange}
          placeholder="Bid Points Per Team"
          required
        />

        <div>
          <h3>Team Names</h3>
          {formData.teams && formData.teams.map((team, index) => (
            <div key={index}>
              <input
                type="text"
                value={team.name}
                onChange={(e) => handleTeamChange(index, e.target.value)}
                placeholder={`Team ${index + 1} Name`}
                required
              />
              <span> - Remaining Points: {team.remainingBidPoints}</span>
            </div>
          ))}
        </div>

        <div>
          <h3>Players</h3>
          {formData.players && formData.players.map((player, index) => (
            <div key={index} style={{ marginBottom: '10px' }}>
              <input
                type="text"
                value={player.name}
                onChange={(e) => handlePlayerChange(index, 'name', e.target.value)}
                placeholder={`Player ${index + 1} Name`}
                required
                disabled={player.done}
              />
              <input
                type="file"
                accept="image/*"
                onChange={(e) => handlePlayerChange(index, 'photo', e.target.files[0])}
                disabled={player.done}
              />
              <select
                value={player.team}
                onChange={(e) => handlePlayerChange(index, 'team', e.target.value)}
                disabled={player.done}
              >
                <option value="">Select Team</option>
                {formData.teams && formData.teams.map((team, idx) => (
                  <option key={idx} value={team.name}>
                    {team.name}
                  </option>
                ))}
              </select>
              <input
                type="number"
                value={player.bidAmount}
                onChange={(e) => handlePlayerChange(index, 'bidAmount', parseInt(e.target.value) || 0)}
                placeholder="Bid Amount"
                disabled={player.done}
              />
              <button
                type="button"
                onClick={() => handleDone(index)}
                disabled={player.done || !player.team || player.bidAmount <= 0}
              >
                {player.done ? 'Done' : 'Mark as Done'}
              </button>
            </div>
          ))}
          <button type="button" onClick={addPlayer}>Add Player</button>
        </div>

        <button type="submit">Submit Tournament</button>
      </form>

      {submittedData && (
        <div>
          <h2>Tournament Information</h2>
          <h3>Tournament: {submittedData.tournamentName}</h3>
          <h4>Teams</h4>
          {submittedData.teams && submittedData.teams.map((team, index) => (
            <div key={index}>
              <h5>{team.name}</h5>
              <p>Remaining Points: {team.remainingBidPoints}</p>
            </div>
          ))}

          <h4>Players</h4>
          {submittedData.players && submittedData.players.map((player, index) => (
            <div key={index} style={{ marginBottom: '10px' }}>
              <p>Name: {player.name}</p>
              {player.photo ? (
                <img
                  src={URL.createObjectURL(player.photo)}
                  alt={`Player ${index + 1} Photo`}
                  style={{ width: '100px', height: '100px', borderRadius: '50%' }}
                />
              ) : (
                <p>No Photo Selected</p>
              )}
              <p>Team: {player.team}</p>
              <p>Bid Amount: {player.bidAmount}</p>
              <p>Status: {player.done ? "Done" : "Pending"}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
