import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function ViewerDashboard() {
  const [auctions, setAuctions] = useState([]); // State to hold the auctions data
  const [loading, setLoading] = useState(true); // State to handle loading
  const [error, setError] = useState(null); // State for error handling
  const [selectedAuction, setSelectedAuction] = useState(null); // State to hold selected auction details

  useEffect(() => {
    // Prevent page reload on first visit
    if (!sessionStorage.getItem('hasVisited')) {
      sessionStorage.setItem('hasVisited', 'true');
      window.location.reload();
    }
  }, []);

  useEffect(() => {
    console.log("Auctions state updated:", auctions);
  }, [auctions]); // This will trigger every time `auctions` changes

  useEffect(() => {
    console.log('Fetching auctions...');
    axios
      .get('http://localhost:5000/api/auctions/')
      .then((response) => {
        console.log('API response:', response.data); // Add log to check API response
        setAuctions(response.data.auctions); // if response.data.auctions is the array
        setLoading(false);
      })
      .catch((err) => {
        console.error('API error:', err); // Log error details
        setError('Failed to fetch auctions');
        setLoading(false);
      });
  }, []); // Run only on component mount

  // Function to fetch detailed information for a selected auction
  const getAuctionDetails = async (auctionId) => {
    try {
      const response = await fetch(`/api/auctions/${auctionId}`);
      const result = await response.json();
      if (result.success) {
        setSelectedAuction(result.auction); // Set the detailed auction data
      } else {
        console.error('Failed to fetch auction details');
      }
    } catch (error) {
      console.error('Error fetching auction details:', error);
    }
  };

  if (loading) {
    return (
      <div className="d-flex justify-content-center align-items-center vh-100">
        <div className="spinner-border text-primary" role="status">
          <span className="sr-only">Loading...</span>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="alert alert-danger text-center" role="alert">
        {error}
      </div>
    );
  }

  return (
    <div className="container mt-4">
      <h1 className="text-center mb-4">Auction Dashboard</h1>
      {Array.isArray(auctions) && auctions.length > 0 ? (
        auctions.map((auction) => {
          console.log('Auction:', auction); // Log each auction data
          return (
            <div key={auction._id} className="card mb-4 shadow-sm">
              <div className="card-header">
                <h2 className="card-title">
                  <button
                    className="btn btn-link"
                    onClick={() => getAuctionDetails(auction._id)} // Fetch details on click
                  >
                    {auction.tournamentName || "No Tournament Name"}
                  </button>
                </h2>
              </div>
              <div className="card-body">
                <p>
                  <strong>Leader Email:</strong> {auction.leaderEmail || "No Email Provided"}
                </p>
                <p>
                  <strong>Bid Points Per Team:</strong> {auction.bidPointsPerTeam || "N/A"}
                </p>

                <h3 className="mt-3">Teams:</h3>
                {auction.teams && auction.teams.length > 0 ? (
                  <div className="row">
                    {auction.teams.map((team, index) => {
                      console.log('Team:', team); // Log each team data
                      return (
                        <div key={index} className="col-md-6">
                          <div className="card mb-3">
                            <div className="card-body">
                              <h4 className="card-title">{team.name || "No Team Name"}</h4>
                              <p>
                                <strong>Bid Points:</strong> {team.bidPoints}
                              </p>
                              <p>
                                <strong>Remaining Bid Points:</strong> {team.remainingBidPoints}
                              </p>
                              {team.image && (
                                <img
                                  src={team.image}
                                  alt={team.name}
                                  className="img-fluid rounded"
                                  style={{ maxWidth: '100px', height: 'auto' }}
                                />
                              )}
                            </div>
                          </div>
                        </div>
                      );
                    })}
                  </div>
                ) : (
                  <p>No teams available.</p>
                )}

                <h3 className="mt-3">Players:</h3>
                {auction.players && auction.players.length > 0 ? (
                  <div className="row">
                    {auction.players.map((player, index) => {
                      console.log('Player:', player); // Add log to check player details
                      return (
                        <div key={index} className="col-md-4">
                          <div className="card mb-3">
                            <div className="card-body text-center">
                              <h4 className="card-title">{player.name || "No Player Name"}</h4>
                              <p>
                                <strong>Bid Amount:</strong> {player.bidAmount || "N/A"}
                              </p>
                              {player.photo && (
                                <img
                                  src={player.photo}
                                  alt={player.name}
                                  className="img-fluid rounded-circle"
                                  style={{ maxWidth: '50px', height: '50px' }}
                                />
                              )}
                            </div>
                          </div>
                        </div>
                      );
                    })}
                  </div>
                ) : (
                  <p>No players listed.</p>
                )}
              </div>
            </div>
          );
        })
      ) : (
        <p className="text-center">No auctions found.</p>
      )}

      {/* Render selected auction details */}
      {selectedAuction && (
        <div className="mt-5">
          <h3>Selected Auction Details</h3>
          <pre>{JSON.stringify(selectedAuction, null, 2)}</pre>
        </div>
      )}
    </div>
  );
}
