import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ViewerDashboard() {
  const [auctions, setAuctions] = useState([]); // State to hold auctions data
  const [loading, setLoading] = useState(true); // Loading state
  const [error, setError] = useState(null); // Error state

  useEffect(() => {
    // Fetch auctions from the API
    const fetchAuctions = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/auctions');
        if (response.data && Array.isArray(response.data)) {
          setAuctions(response.data); // Set the auctions to state
        } else {
          setError('Unexpected data format'); // Handle unexpected API response
        }
      } catch (err) {
        setError(err.message || 'Failed to fetch data'); // Handle errors
      } finally {
        setLoading(false); // Disable loading
      }
    };

    fetchAuctions();
  }, []); // Empty dependency array ensures it runs once on mount

  // Loading state
  if (loading) {
    return <div>Loading...</div>;
  }

  // Error state
  if (error) {
    return <div>Error: {error}</div>;
  }

  // Render auctions data
  return (
    <div>
      <h1>Auction Dashboard</h1>
      {auctions.length > 0 ? (
        auctions.map((auction) => (
          <div key={auction._id} className="auction-card" style={{ border: '1px solid #ccc', margin: '10px', padding: '10px' }}>
            <h2>{auction.tournamentName || 'No Tournament Name'}</h2>
            <p><strong>Leader Email:</strong> {auction.leaderEmail || 'No Leader Email'}</p>
            <p><strong>Total Points per Team:</strong> {auction.bidPointsPerTeam || 'No Points Data'}</p>

            {/* Teams Section */}
            <h3>Teams</h3>
            {auction.teams && auction.teams.length > 0 ? (
              <ul>
                {auction.teams.map((team) => (
                  <li key={team._id} style={{ marginBottom: '10px' }}>
                    <p><strong>Team Name:</strong> {team.name || 'No Team Name'}</p>
                    <p><strong>Bid Points:</strong> {team.bidPoints || 'No Bid Points'}</p>
                    <p><strong>Remaining Points:</strong> {team.remainingBidPoints || 'No Remaining Points'}</p>
                  </li>
                ))}
              </ul>
            ) : (
              <p>No teams available for this auction.</p>
            )}

            {/* Players Section */}
            <h3>Players</h3>
            {auction.players && auction.players.length > 0 ? (
              <ul>
                {auction.players.map((player) => (
                  <li key={player._id} style={{ marginBottom: '10px' }}>
                    <p><strong>Player Name:</strong> {player.name || 'No Name'}</p>
                    <p><strong>Bid Amount:</strong> {player.bidAmount || 'No Bid Amount'}</p>
                    <p><strong>Team:</strong> {player.team || 'No Team'}</p>
                    <p><strong>Status:</strong> {player.done ? 'Done' : 'Pending'}</p>
                    {player.photo && (
                      <img src={player.photo} alt={`${player.name}'s photo`} width="100" style={{ marginTop: '5px' }} />
                    )}
                  </li>
                ))}
              </ul>
            ) : (
              <p>No players added to this auction yet.</p>
            )}
          </div>
        ))
      ) : (
        <p>No auctions found.</p>
      )}
    </div>
  );
}

export default ViewerDashboard;
