import React, { useState } from 'react';

export default function TournamentForm() {
  const [formData, setFormData] = useState({
    leaderEmail: '',
    tournamentName: '',
    teamsCount: 0,
    bidPointsPerTeam: 0,
    teams: [],
  });

  // Handle input change
  const handleChange = (e) => {
    const { name, value } = e.target;
    const newValue = name === 'teamsCount' ? parseInt(value) : value;

    setFormData((prevData) => {
      // Update teams array based on teamsCount
      if (name === 'teamsCount') {
        const updatedTeams = Array.from(
          { length: newValue || 0 },
          (_, index) => prevData.teams[index] || ''
        );
        return {
          ...prevData,
          teamsCount: newValue,
          teams: updatedTeams,
        };
      }

      return {
        ...prevData,
        [name]: newValue,
      };
    });
  };

  // Handle team names input change
  const handleTeamChange = (index, value) => {
    const updatedTeams = [...formData.teams];
    updatedTeams[index] = value;
    setFormData({
      ...formData,
      teams: updatedTeams,
    });
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form submitted:', formData);
  };

  return (
    <div>
      <h1>Tournament Form</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          name="leaderEmail"
          value={formData.leaderEmail}
          onChange={handleChange}
          placeholder="Leader Email"
          required
        />
        <input
          type="text"
          name="tournamentName"
          value={formData.tournamentName}
          onChange={handleChange}
          placeholder="Tournament Name"
          required
        />
        <input
          type="number"
          name="teamsCount"
          value={formData.teamsCount}
          onChange={handleChange}
          placeholder="Number of Teams"
          required
        />
        <input
          type="number"
          name="bidPointsPerTeam"
          value={formData.bidPointsPerTeam}
          onChange={handleChange}
          placeholder="Bid Points Per Team"
          required
        />

        {/* Render Team Names dynamically */}
        <div>
          <h3>Team Names</h3>
          {formData.teams.map((team, index) => (
            <div key={index}>
              <input
                type="text"
                value={team}
                onChange={(e) => handleTeamChange(index, e.target.value)}
                placeholder={`Team ${index + 1} Name`}
                required
              />
            </div>
          ))}
        </div>

        <button type="submit">Submit Tournament</button>
      </form>
    </div>
  );
}
