// src/components/AdminSidebar.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const AdminSidebar = () => {
    // State to track sidebar visibility and position
    const [isSidebarVisible, setIsSidebarVisible] = useState(false);
    const [isSidebarRight, setIsSidebarRight] = useState(false);

    // Toggle visibility and position of sidebar
    const toggleSidebar = () => {
        setIsSidebarVisible(!isSidebarVisible);
    };

    const togglePosition = () => {
        setIsSidebarRight(!isSidebarRight);
    };

    return (
        <div>
            {/* Button to toggle sidebar */}
            <button className="toggle-btn" onClick={toggleSidebar}>
                {isSidebarVisible ? 'Hide Sidebar' : 'Show Sidebar'}
            </button>
            {/* Button to toggle sidebar position */}
            <button className="toggle-position-btn" onClick={togglePosition}>
                {isSidebarRight ? 'Move Sidebar to Left' : 'Move Sidebar to Right'}
            </button>

            {/* Sidebar container */}
            {isSidebarVisible && (
                <aside className={`admin-sidebar ${isSidebarRight ? 'right' : 'left'}`}>
                    <h3 className="sidebar-title">Admin Dashboard</h3>
                    <nav className="sidebar-nav">
                        <ul>
                            <li>
                                <Link to="/admin-sidebar" className="sidebar-link">Dashboard Overview</Link>
                            </li>
                            <li>
                                <Link to="/admin-dashboard/users" className="sidebar-link">Users</Link>
                            </li>
                            <li>
                                <Link to="/admin-dashboard/auctions" className="sidebar-link">Manage Auctions</Link>
                            </li>
                            <li>
                                <Link to="/admin-dashboard/reports" className="sidebar-link">Reports</Link>
                            </li>
                            <li>
                                <Link to="/admin-dashboard/settings" className="sidebar-link">Settings</Link>
                            </li>
                        </ul>
                    </nav>
                </aside>
            )}
        </div>
    );
};

export default AdminSidebar;
