import React, { useState, useEffect } from 'react';

const AdminDashboard = () => {
    const [leaders, setLeaders] = useState([]);
    const [users, setUsers] = useState([]);
    const [totalUsers, setTotalUsers] = useState(0);
    const [totalLeaders, setTotalLeaders] = useState(0);
    const [roleRequests, setRoleRequests] = useState([]);

    // Fetch all data when the component mounts
    useEffect(() => {
        const fetchData = async () => {
            try {
                // Fetch leaders, users, stats, and role change requests
                const leadersResponse = await fetch('http://localhost:5000/api/admin/leaders');
                const leadersData = await leadersResponse.json();

                const usersResponse = await fetch('http://localhost:5000/api/admin/users');
                const usersData = await usersResponse.json();

                const statsResponse = await fetch('http://localhost:5000/api/admin/stats');
                const statsData = await statsResponse.json();

                const requestsResponse = await fetch('http://localhost:5000/api/api/role-requests');
                const requestsData = await requestsResponse.json();

                setLeaders(leadersData);
                setUsers(usersData);
                setTotalUsers(statsData.totalUsers);
                setTotalLeaders(statsData.totalLeaders);
                setRoleRequests(requestsData);
            } catch (error) {
                console.error("Error fetching data:", error);
            }
        };
        fetchData();
    }, []);

    // Handle approving a request
    const handleApproveRequest = async (requestId) => {
        try {
            await fetch(`http://localhost:5000/api/admin/approve-role/${requestId}`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
            });
            setRoleRequests(prevRequests => prevRequests.filter(request => request._id !== requestId));
            alert("Request approved successfully.");
        } catch (error) {
            console.error("Error approving request:", error);
        }
    };

    // Handle rejecting a request
    const handleRejectRequest = async (requestId) => {
        try {
            await fetch(`http://localhost:5000/api/admin/reject-role/${requestId}`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
            });
            setRoleRequests(prevRequests => prevRequests.filter(request => request._id !== requestId));
            alert("Request rejected.");
        } catch (error) {
            console.error("Error rejecting request:", error);
        }
    };

    return (
        <div className="dashboard">
            <h2>Admin Dashboard</h2>
            <div className="stats">
                <p>Total Users: {totalUsers}</p>
                <p>Total Leaders: {totalLeaders}</p>
            </div>

            <h3>Users and Leaders Details</h3>
            <table>
                <thead>
                    <tr>
                        <th>Email</th>
                        <th>Role</th>
                        <th>Payment</th>
                        <th>Verification</th>
                    </tr>
                </thead>
                <tbody>
                    {[...users, ...leaders].map(user => (
                        <tr key={user._id}>
                            <td>{user.email}</td>
                            <td>{user.role}</td>
                            <td>{user.payment}</td>
                            <td>{user.verificationStatus ? 'Verified' : 'Pending'}</td>
                        </tr>
                    ))}
                </tbody>
            </table>

            <h3>Role Change Requests</h3>
            <table>
                <thead>
                    <tr>
                        <th>Full Name</th>
                        <th>Email</th>
                        <th>Tournament Name</th>
                        <th>Tournament Date</th>
                        <th>Auction Date</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {roleRequests.map(request => (
                        <tr key={request._id}>
                            <td>{request.fullName}</td>
                            <td>{request.email}</td>
                            <td>{request.tournamentName}</td>
                            <td>{new Date(request.tournamentDate).toLocaleDateString()}</td>
                            <td>{new Date(request.auctionDate).toLocaleDateString()}</td>
                            <td>
                                <button onClick={() => handleApproveRequest(request._id)}>Approve</button>
                                <button onClick={() => handleRejectRequest(request._id)}>Reject</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default AdminDashboard;
