import React, { useState } from 'react';
import axios from 'axios';

export default function LeaderDashboard() {
  const [auctionData, setAuctionData] = useState({
    leaderEmail: '',
    tournamentName: '',
    tournamentDate: '',
    teamsCount: 0,
    totalPointsPerTeam: 0,
    teams: [], // Store the teams and points
    players: [], // Store players with their details
    image: null, // For handling the uploaded image
  });

  // Function to handle input change
  const handleChange = (e) => {
    const { name, value } = e.target;
    setAuctionData({
      ...auctionData,
      [name]: value,
    });
  };

  // Handle file input change
  const handleImageChange = (e) => {
    setAuctionData({
      ...auctionData,
      image: e.target.files[0], // Store the selected image file
    });
  };

  // Function to handle player input change
  const handlePlayerChange = (e, index) => {
    const { name, value } = e.target;
    const updatedPlayers = [...auctionData.players];
    updatedPlayers[index] = { ...updatedPlayers[index], [name]: value };
    setAuctionData({
      ...auctionData,
      players: updatedPlayers,
    });
  };

  // Function to add a new player form
  const addPlayer = () => {
    setAuctionData({
      ...auctionData,
      players: [
        ...auctionData.players,
        {
          name: '',
          number: 0,
          team: '',
          bidPoints: 0,
          profilePic: '',
        },
      ],
    });
  };

  // Function to remove a player form
  const removePlayer = (index) => {
    const updatedPlayers = auctionData.players.filter((_, i) => i !== index);
    setAuctionData({
      ...auctionData,
      players: updatedPlayers,
    });
  };

  // Handle team input change
  const handleTeamChange = (e, index) => {
    const { name, value } = e.target;
    const updatedTeams = [...auctionData.teams];
    updatedTeams[index] = { ...updatedTeams[index], [name]: value };
    setAuctionData({
      ...auctionData,
      teams: updatedTeams,
    });
  };

  // Function to add a new team form
  const addTeam = () => {
    setAuctionData({
      ...auctionData,
      teams: [
        ...auctionData.teams,
        {
          teamName: '',
          points: 0,
        },
      ],
    });
  };

  // Function to remove a team form
  const removeTeam = (index) => {
    const updatedTeams = auctionData.teams.filter((_, i) => i !== index);
    setAuctionData({
      ...auctionData,
      teams: updatedTeams,
    });
  };

  // Function to handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const formData = new FormData();
      formData.append('leaderEmail', auctionData.leaderEmail);
      formData.append('tournamentName', auctionData.tournamentName);
      formData.append('tournamentDate', auctionData.tournamentDate);
      formData.append('teamsCount', auctionData.teamsCount);
      formData.append('totalPointsPerTeam', auctionData.totalPointsPerTeam);

      // Append players array
      auctionData.players.forEach((player, index) => {
        formData.append(`players[${index}][name]`, player.name);
        formData.append(`players[${index}][number]`, player.number);
        formData.append(`players[${index}][team]`, player.team);
        formData.append(`players[${index}][bidPoints]`, player.bidPoints);
        formData.append(`players[${index}][profilePic]`, player.profilePic);
      });

      // Append teams array
      auctionData.teams.forEach((team, index) => {
        formData.append(`teams[${index}][teamName]`, team.teamName);
        formData.append(`teams[${index}][points]`, team.points);
      });

      // Add image if available
      if (auctionData.image) {
        formData.append('image', auctionData.image);
      }

      const response = await axios.post('http://localhost:5000/api/auctions/create-auction', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      // Handle response after success
      console.log('Auction created successfully', response.data);
    } catch (error) {
      console.error('Error creating auction:', error);
    }
  };

  return (
    <div>
      <h1>Create New Auction</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          name="leaderEmail"
          value={auctionData.leaderEmail}
          onChange={handleChange}
          placeholder="Leader Email"
          required
        />
        <input
          type="text"
          name="tournamentName"
          value={auctionData.tournamentName}
          onChange={handleChange}
          placeholder="Tournament Name"
          required
        />
        <input
          type="date"
          name="tournamentDate"
          value={auctionData.tournamentDate}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          name="teamsCount"
          value={auctionData.teamsCount}
          onChange={handleChange}
          placeholder="Teams Count"
          required
        />
        <input
          type="number"
          name="totalPointsPerTeam"
          value={auctionData.totalPointsPerTeam}
          onChange={handleChange}
          placeholder="Total Points Per Team"
          required
        />

        {/* Render Team Info dynamically */}
        <div>
          <h3>Teams Info</h3>
          {auctionData.teams.map((team, index) => (
            <div key={index}>
              <h4>Team {index + 1}</h4>
              <input
                type="text"
                name="teamName"
                value={team.teamName}
                onChange={(e) => handleTeamChange(e, index)}
                placeholder="Team Name"
                required
              />
              <input
                type="number"
                name="points"
                value={team.points}
                onChange={(e) => handleTeamChange(e, index)}
                placeholder="Points"
                required
              />
              <button type="button" onClick={() => removeTeam(index)}>Remove Team</button>
            </div>
          ))}
          <button type="button" onClick={addTeam}>Add Another Team</button>
        </div>

        {/* Render Player Info dynamically */}
        <div>
          <h3>Player Info</h3>
          {auctionData.players.map((player, index) => (
            <div key={index}>
              <h4>Player {index + 1}</h4>
              <input
                type="text"
                name="name"
                value={player.name}
                onChange={(e) => handlePlayerChange(e, index)}
                placeholder="Player Name"
                required
              />
              <input
                type="number"
                name="number"
                value={player.number}
                onChange={(e) => handlePlayerChange(e, index)}
                placeholder="Player Number"
                required
              />
              <input
                type="text"
                name="profilePic"
                value={player.profilePic}
                onChange={(e) => handlePlayerChange(e, index)}
                placeholder="Profile Picture URL"
              />
              <select
                name="team"
                value={player.team}
                onChange={(e) => handlePlayerChange(e, index)}
                required
              >
                <option value="">Select Team</option>
                {auctionData.teams.map((team, idx) => (
                  <option key={idx} value={team.teamName}>
                    {team.teamName} ({team.points} Points)
                  </option>
                ))}
              </select>
              <button type="button" onClick={() => removePlayer(index)}>Remove Player</button>
            </div>
          ))}
          <button type="button" onClick={addPlayer}>Add Another Player</button>
        </div>

        {/* Image Upload */}
        <input
          type="file"
          onChange={handleImageChange}
        />
        <button type="submit">Create Auction</button>
      </form>

      {/* Display the list of players and their details */}
      <h3>Players Summary</h3>
      {auctionData.players.map((player, index) => (
        <div key={index}>
          <img src={player.profilePic} alt={player.name} width="50" />
          <h4>{player.name} - {player.team}</h4>
          <p>Number: {player.number} | Points: {player.bidPoints}</p>
        </div>
      ))}
    </div>
  );
}
