import React, { useState, useEffect } from 'react';

const LeaderDashboard = ({ auctionId }) => {
  const [newPlayerName, setNewPlayerName] = useState('');
  const [newPlayerPhoto, setNewPlayerPhoto] = useState('');
  const [newPlayerBidAmount, setNewPlayerBidAmount] = useState('');
  const [newPlayerTeam, setNewPlayerTeam] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    const playerData = {
      name: newPlayerName,
      photo: newPlayerPhoto,
      bidAmount: newPlayerBidAmount,
      team: newPlayerTeam,
    };

    try {
      const response = await fetch(`/api/auctions/${auctionId}/players`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(playerData),
      });
      const result = await response.json();

      if (result.success) {
        // Handle successful submission (e.g., reset form or notify user)
        console.log('Player added successfully');
      } else {
        console.error('Failed to add player');
      }
    } catch (error) {
      console.error('Error adding player:', error);
    }
  };

  return (
    <div className="container mt-4">
      <h1>Leader Dashboard</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Player Name:</label>
          <input
            type="text"
            className="form-control"
            value={newPlayerName}
            onChange={(e) => setNewPlayerName(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label>Player Photo URL:</label>
          <input
            type="text"
            className="form-control"
            value={newPlayerPhoto}
            onChange={(e) => setNewPlayerPhoto(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label>Bid Amount:</label>
          <input
            type="number"
            className="form-control"
            value={newPlayerBidAmount}
            onChange={(e) => setNewPlayerBidAmount(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label>Player Team:</label>
          <input
            type="text"
            className="form-control"
            value={newPlayerTeam}
            onChange={(e) => setNewPlayerTeam(e.target.value)}
            required
          />
        </div>
        <button type="submit" className="btn btn-primary">Add Player</button>
      </form>
    </div>
  );
};

export default LeaderDashboard;
